  /**
   * Overridding parent method, we need to check
   * if the icon family is trully enabled or not first
   * before fetching the data.
   *
   * @param $keys
   * @param bool|FALSE $sanitize
   */
  public function get($keys, $sanitize = false) {
    if (isset($this->options[$keys]) && !$this->get($keys . '.enabled')) {
      return null;
    }

    return parent::get($keys, $sanitize);
  }

  public function getFontListOptions() {
    $options = array(
      false => __('-- Select --', 'victheme_core'),
    );

    foreach ($this->extract() as $family => $data) {

      if (!$this->get($family . '.enabled')) {
        continue;
      }

      if ($this->get($family . '.text')) {
        $options[$family] = $this->get($family . '.text');
      }
      else {
        $options[$family] = $family;
      }
    }

    return $options;
  }

